muclient.script:
function OnShowVersion (a, b, c)
  {
  world.note ("Script engine = "+ ScriptEngine ());
  world.note ("Version = " + ScriptEngineMajorVersion () +
             "." + ScriptEngineMinorVersion ());
  world.note ("Build  = " + ScriptEngineBuildVersion ());  
  }


function OnPluginInstall ()
  {
  OnShowVersion (0, 0, 0);
  }
